use hex_literal::hex;
use mdbx::{DatabaseFlags, WriteFlags};

fn main() {
    let tmp = tempfile::tempdir().unwrap();
    let env = mdbx::Environment::new()
        .set_max_dbs(1)
        .open(tmp.path())
        .unwrap();

    let tx = env.begin_rw_txn().unwrap();
    let db = tx.create_db(Some("test"), DatabaseFlags::DUP_SORT).unwrap();

    tx.put(&db, hex!("2a0597e665ac3d1cabeede95cedf907934db7f639e477b3c77b242140d8cf728"), hex!(""), WriteFlags::default()).unwrap();

    tx.commit().unwrap();
}
